(window.webpackJsonp=window.webpackJsonp||[]).push([[18],{393:function(n,h,i){"use strict";i.r(h);var t=i(1),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("p",[n._v("“Ngàn vạn dặm bắt đầu từ một bước chân” - Ngạn ngữ")]),n._v(" "),i("p",[n._v("Có bao giờ là muộn nếu bạn tìm thấy mục tiêu, mơ ước của mình. Việc khó nhất luôn là bước chân đầu tiên. Quay trở lại với câu chuyện của chính mình, một vài suy nghĩ vu vơ được viết vào chiều thứ 7 bình yên, tôi mong bạn sẽ có được một vài góc nhìn khác trong cuộc sống, và mong sao sẽ giúp ích được cho bạn gì đó …")]),n._v(" "),i("p",[n._v("Ngày cuối tuần - thứ 7 bắt đầu như mọi ngày, vẫn là một sáng dậy muộn vì người mệt lả, vẫn là những buổi ăn sáng bên ngoài ở hàng quán gần nhà. Cơ thể tôi như một cái máy cứ lặp đi lặp lại như vậy, tôi tự hỏi: “Đã bao lâu rồi mình chưa về nhà?”")]),n._v(" "),i("p",[n._v("Để cho bạn nào chưa biết, thì tôi là Hoàng, hiện đang làm về lĩnh vực IT (vua của mọi nghề *cười nhẹ). Quê tôi ở Hưng Yên, và như bao người khác, tôi lên Hà Nội học tập và làm việc.")]),n._v(" "),i("p",[n._v("Sau khi ăn sáng xong, thì đồng hồ cũng đã điểm 11h30. Tôi quyết định chuẩn bị đồ đạc và về quê, về nơi mà chất chứa rất nhiều kỉ niệm mà tôi không muốn nhìn lại. Nói là làm, giữa cái nóng như đổ lửa, tôi phi từ HN về HY trên chiếc xe hàng ngày đi làm. Vật chất quyết định ý thức, hành trình của tôi ngoài việc nhìn đồng hồ xem còn bao xa, thì chẳng có gì khác ngoài mệt nhọc vì nắng nóng.")]),n._v(" "),i("p",[n._v("Đừng quan tâm tới khoảng thời gian ở giữa, bây giờ là 17h30 chiều, và tôi đang ở gần công viên nhà mình, một chút lắng đọng, một chút bình yên, và chúng ta đang ở đây với nhau. Cảm ơn các bạn")]),n._v(" "),i("p",[n._v("Quay lại với vấn đề chính, việc khó nhất trong một hành trình, luôn luôn là bước chân đầu tiên. Tôi luôn muốn dành lời khuyên này cho chính mình khi còn trẻ là: “Hãy suy nghĩ thật kĩ càng trước khi hành động, và khi hành động thì đừng suy nghĩ.” Đại ý ở đây muốn nói, thật khó để đi đến một quyết định gì đó, nhất là đối với các bạn trẻ, chưa có nhiều trải nghiệm, cộng với việc chúng ta đang ở trong thời đại có quá nhiều thứ làm xao nhãng bản thân. Vậy nên, hãy dành thời gian cho việc đó. Và đương nhiên, khi đã có cho mình sự lựa chọn, chúng ta hãy làm, và đừng nghĩ lại rằng liệu quyết định của mình có đúng không? Đừng nhìn lại.")]),n._v(" "),i("p",[n._v("Một vấn đề nữa, tôi muốn nói đến với mình hiện tại: “Ước mơ của bạn là gì?” Tôi không biết các bạn thế nào, nhưng đối với tôi hiện tại, công việc ổn định (mức thu nhập theo tôi là ổn). Hàng ngày đều thế, bận bịu trong guồng quay cuộc sống, công việc, bỗng chốc thấy thời gian trôi nhanh quá, bẵng cái đã một tháng, bẵng cái đã một năm, bẵng cái … là cả một đời người.")]),n._v(" "),i("p",[n._v("Trong mọi trường hợp, hãy luôn cho mình quyền có thể đứng dậy và từ bỏ mọi thứ hiện tại. Tôi biết, để làm được điều này, bạn cần tích luỹ rất nhiều về mặt tài chính, quan hệ, … Nhưng nếu bạn làm có quyền này, tôi chắc chắn trong mọi trường hợp bạn sẽ luôn là người chủ động.")]),n._v(" "),i("p",[n._v("Suy cho cùng thì, chúng ta mưu cầu điều gì trong cuộc sống? Tôi hiện giờ cũng chưa biết nữa, nhưng chỉ là, “Ước mơ của bạn là gì?”")])])}),[],!1,null,null,null);h.default=c.exports}}]);